                    <label htmlFor={"input-" + channelId}>{channelId + 1}</label>
                </>
    )
};

const PresetEditor = (props: {
    soundCard: SoundCard
}) => {
    const {soundCard} = props;
    const {presets} = useStage();

    return (
        <div>
            <h3>{soundCard.name}</h3>
            <ul>
                <li>
                    {soundCard.numInputChannels} input channels
                </li>
                <li>
                    {soundCard.numOutputChannels} output channels
                </li>
                <li>
                    {soundCard.sampleRate} Hz sample rate
                </li>
            </ul>
            <ul>
                <li>
                    {presets.bySoundCard[soundCard._id] && presets.bySoundCard[soundCard._id].map(id => presets.byId[id]).map(preset => (
                        <div>
                            {preset.name}
                            <p>
                                Input:
                                <ul>
                                    <li>
                                        {Array.from(Array(soundCard.numInputChannels).keys()).map((channelId) => (
                                            <>
                                                <input id={"input-" + channelId} type="checkbox" value={channelId}/>
                                                <label htmlFor={"input-" + channelId}>{channelId + 1}</label>
                                            </>
                                        ))}
                                    </li>
                                </ul>
                            </p>
                            <p>
                                Output:
                                <ul>
                                    <li>
                                        {Array.from(Array(soundCard.numOutputChannels).keys()).map((channelId) => (
                                            <>
                                                <input id={"output-" + channelId} type="checkbox" value={channelId}/>
                                                <label htmlFor={"output-" + channelId}>{channelId + 1}</label>
                                            </>
                                        ))}
                                    </li>
                                </ul>
                            </p>
                        </div>
                    ))}
                </li>
            </ul>
        </div>
    )
};
export default PresetEditor;
