import {useEffect, useReducer, useState} from "react";
import useAuth from "../libs/useAuth";
import {ITeckosClient, TeckosClientWithJWT} from 'teckos-client';
import {Device} from "../utils/types/Device";
import {ServerDeviceEvents, ServerGlobalEvents, ServerStageEvents} from "../utils/types/SocketEvents";
import SoundCardReducer from "../utils/reducers/SoundCardReducer";
import StageMemberReducer, {
    addStageMember,
    changeStageMember,
    removeStageMember
} from "../utils/reducers/StageMemberReducer";
import CustomStageMemberReducer from "../utils/reducers/CustomStageMemberReducer";
import TrackPresetReducer from "../utils/reducers/TrackPresetReducer";
import RemoteTrackReducer from "../utils/reducers/RemoteTrackReducer";
import TrackReducer from "../utils/reducers/TrackReducer";
import {InitialStagePackage} from "../utils/types/InitialStagePackage";

const email = "test@digital-stage.org";
const password = "testtesttest";


const Index = () => {
    const {signInWithEmailAndPassword, token} = useAuth();
    const [socket, setSocket] = useState<ITeckosClient>();
    const [device, setDevice] = useState<Device>();

    // OV related entities
    const [soundCards, dispatchSoundCard] = useReducer(SoundCardReducer, {
        byId: {},
        allIds: []
    });
    const [presets, dispatchPresets] = useReducer(TrackPresetReducer, {
        byId: {},
        bySoundCard: {},
        allIds: []
    });
    const [tracks, dispatchTracks] = useReducer(TrackReducer, {
        byId: {},
        bySoundCard: {},
        byPreset: {},
        allIds: []
    });

    // Digital stage related entities
    const [stageMembers, dispatchStageMembers] = useReducer(StageMemberReducer, {
        byId: {},
        byGroup: {},
        allIds: []
    });
    const [customStageMembers, dispatchCustomStageMembers] = useReducer(CustomStageMemberReducer, {
        byId: {},
        byStageMember: {},
        allIds: []
    });
    const [remoteTracks, dispatchRemoteTracks] = useReducer(RemoteTrackReducer, {
        byId: {},
        byStageMember: {},
        allIds: []
    });

    useEffect(() => {
        // Sign in
        signInWithEmailAndPassword(email, password);
    }, []);

    useEffect(() => {
        // Got token, register self as device
        const initialDevice: Partial<Device> = {
            name: 'OV Simulator',
            canOv: true,
            canAudio: true,
            sendAudio: true,
            receiveAudio: true,
            soundCardIds: [],
        };

        const nSocket = new TeckosClientWithJWT(
            process.env.NEXT_PUBLIC_API_URL,
            {
                reconnection: true,
                timeout: 1000,
            },
            token,
            {
                device: initialDevice,
            }
        );

        nSocket.on("connect", () => {
            console.log("connected!")
        });


        nSocket.on(ServerDeviceEvents.LOCAL_DEVICE_READY, (device: Device) => {
            console.log("connected!")
            setDevice(device);
        })

        nSocket.on(ServerStageEvents.STAGE_MEMBER_ADDED, payload => dispatchStageMembers(addStageMember(payload)));
        nSocket.on(ServerStageEvents.STAGE_MEMBER_CHANGED, payload => dispatchStageMembers(changeStageMember(payload)));
        nSocket.on(ServerStageEvents.STAGE_MEMBER_REMOVED, payload => dispatchStageMembers(removeStageMember(payload)));

        nSocket.on(ServerStageEvents.CUSTOM_STAGE_MEMBER_ADDED, payload => dispatchCustomStageMembers({
            type: ServerStageEvents.CUSTOM_STAGE_MEMBER_ADDED,
            payload: payload
        }));
        nSocket.on(ServerStageEvents.CUSTOM_STAGE_MEMBER_CHANGED, payload => dispatchCustomStageMembers({
            type: ServerStageEvents.CUSTOM_STAGE_MEMBER_CHANGED,
            payload: payload
        }));
        nSocket.on(ServerStageEvents.CUSTOM_STAGE_MEMBER_REMOVED, payload => dispatchCustomStageMembers({
            type: ServerStageEvents.CUSTOM_STAGE_MEMBER_REMOVED,
            payload: payload
        }));

        nSocket.on(ServerGlobalEvents.STAGE_JOINED, (payload: InitialStagePackage) => {
            // Handle package with many stage related entities
            payload.stageMembers.forEach(stageMember => dispatchStageMembers(addStageMember(stageMember)));
            payload.customStageMembers.forEach(customStageMember => dispatchCustomStageMembers({
                type: ServerStageEvents.CUSTOM_STAGE_MEMBER_ADDED,
                payload: customStageMember
            }));
            payload.ovTracks.forEach(ovTrack => dispatchRemoteTracks({
                type: ServerStageEvents.STAGE_MEMBER_OV_ADDED,
                payload: ovTrack
            }));
        });

        nSocket.connect();

        setSocket(nSocket);

    }, [token])

    return (
        <div>
            <ul>
                <li>
                    Token: {token ? "ok" : "requesting"}
                </li>
                <li>
                    Device:
                    {device ? <pre>{JSON.stringify(device, null, 2)}</pre> : "requesting"}
                </li>
                <li>
                    Sound cards:
                    <pre>
                    {JSON.stringify(soundCards, null, 2)}
                    </pre>
                </li>
                <li>
                    Stage members:
                    <pre>
                    {JSON.stringify(stageMembers, null, 2)}
                    </pre>
                </li>
                <li>
                    Custom stage members:
                    <pre>
                    {JSON.stringify(customStageMembers, null, 2)}
                    </pre>
                </li>
            </ul>
        </div>
    )
};
export default Index;
